mapper.insert:
INSERT INTO SPRING_BOARD(
	SEQ
	, TITLE
	, CONTENT
	, WRITER
) VALUES (
	(SELECT NVL(MAX(SEQ) +1 ,1) FROM SPRING_BOARD)
	, #{title}
	, #{content}
	, #{writer}
)

mapper.update:
UPDATE SPRING_BOARD
SET
TITLE = #{title}
, WRITER = #{writer}
, CONTENT = #{content]
WHERE SEQ = #{seq]

UPDATE SPRING_BOARD
SET READ_CNT = READ_CNT + 1
WHERE SEQ= #{seq}
